import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import { fetchMyWorkouts } from "../utils/api";
import Loader from "./Loader";

const MyWorkouts = () => {
  const [workouts, setWorkouts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { accessToken } = useAuth(); // Get token

  useEffect(() => {
    const loadWorkouts = async () => {
      try {
        const data = await fetchMyWorkouts(accessToken);
        setWorkouts(data);
      } catch (err) {
        setError(err.message || "Не удалось загрузить тренировки");
      } finally {
        setLoading(false);
      }
    };

    if (accessToken) {
      loadWorkouts();
    }
  }, [accessToken]);

  if (loading) {
    return <Loader />;
  }

  if (error) {
    return <div className="container mt-4"><div className="alert alert-danger">{error}</div></div>;
  }

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Мои тренировки</h1>
      <div className="d-flex flex-column align-items-center gap-4">
        {workouts.map((workout) => (
          <div key={workout.id} className="card w-75">
            <div className="card-body">
              <h2 className="card-title">
                {workout.name || `Тренировка от ${workout.date}`}
              </h2>
              <p className="card-text">
                <strong>Проработанные мышцы:</strong>{" "}
                {workout.muscles_involved?.length
                  ? workout.muscles_involved.join(", ")
                  : "Не указано"}
              </p>
            </div>
            <div className="card-footer text-center">
              <Link to={`/workouts/${workout.id}`} className="btn btn-primary">
                Подробнее...
              </Link>
            </div>
          </div>
        ))}
      </div>
      <div className="text-center mt-4">
        <Link to="/workouts/new" className="btn btn-success">
          Добавить тренировку
        </Link>
      </div>
    </div>
  );
};

export default MyWorkouts;
