import React, { useEffect, useState } from "react";
import api from "../api";
import { Link } from "react-router-dom";
import useAuth from "../hooks/useAuth";


const ExerciseList = () => {
  const [exercises, setExercises] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isAdmin } = useAuth();

  useEffect(() => {
    api.get("/exercises/")
      .then((response) => {
        setExercises(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Ошибка при загрузке упражнений:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Загрузка...</p>;
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center">Список упражнений</h1>
      <div className="card">
        <div className="card-body">
          {/* Desktop table view */}
          <table className="table d-none d-md-table">
            <thead>
              <tr>
                <th>Название</th>
                <th>Группа мышц</th>
                <th>Описание</th>
              </tr>
            </thead>
            <tbody>
              {exercises.map((exercise) => (
                <tr key={exercise.id}>
                  <td>
                    {isAdmin ? (
                      <Link to={`/exercises/${exercise.id}`}>{exercise.name}</Link>
                    ) : (
                      <span>{exercise.name}</span>
                    )}
                  </td>
                  <td>{exercise.muscle_group}</td>
                  <td>{exercise.description}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Mobile card view */}
          <div className="d-md-none">
            {exercises.map((exercise) => (
              <div key={exercise.id} className="card mb-3">
                <div className="card-body">
                  <h5 className="card-title mb-2">
                    {isAdmin ? (
                      <Link to={`/exercises/${exercise.id}`}>{exercise.name}</Link>
                    ) : (
                      <span>{exercise.name}</span>
                    )}
                  </h5>
                  <p className="card-text mb-1">
                    <strong>Группа мышц:</strong> {exercise.muscle_group}
                  </p>
                  <p className="card-text mb-0">
                    <strong>Описание:</strong> {exercise.description}
                  </p>
                </div>
              </div>
            ))}
          </div>

          {!isAdmin && (
            <div className="text-center mt-3">
              <Link to='/add-exercise' className="btn btn-success">
                + Добавить упражнение
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ExerciseList;
