import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import useAuth from "../hooks/useAuth";
import { fetchMyWorkouts, fetchExercises, createWorkoutPlan } from "../utils/api";
import Loader from "./Loader";

const MyWorkouts = () => {
  const [workouts, setWorkouts] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showPlanForm, setShowPlanForm] = useState(false);
  const [selectedExercise, setSelectedExercise] = useState("");
  const [startDate, setStartDate] = useState("");
  const [frequencyDays, setFrequencyDays] = useState(1);
  const [planMessage, setPlanMessage] = useState("");
  const { accessToken } = useAuth();

  useEffect(() => {
    const loadData = async () => {
      try {
        const [workoutsData, exercisesData] = await Promise.all([
          fetchMyWorkouts(accessToken),
          fetchExercises()
        ]);
        setWorkouts(workoutsData);
        setExercises(exercisesData);
      } catch (err) {
        setError(err.message || "Не удалось загрузить данные");
      } finally {
        setLoading(false);
      }
    };

    if (accessToken) {
      loadData();
    }
  }, [accessToken]);

  const handleCreatePlan = async (e) => {
    e.preventDefault();
    setPlanMessage("");

    if (!selectedExercise || !startDate) {
      setPlanMessage("Пожалуйста, заполните все поля");
      return;
    }

    try {
      await createWorkoutPlan(accessToken, {
        exercise: parseInt(selectedExercise),
        start_date: startDate,
        frequency_days: frequencyDays,
        is_active: true
      });

      setPlanMessage("План тренировки успешно создан!");
      setShowPlanForm(false);
      setSelectedExercise("");
      setStartDate("");
      setFrequencyDays(1);

      // Перезагружаем данные
      const workoutsData = await fetchMyWorkouts(accessToken);
      setWorkouts(workoutsData);
    } catch (err) {
      setPlanMessage(err.message || "Не удалось создать план тренировки");
    }
  };

  if (loading) {
    return <Loader />;
  }

  if (error) {
    return <div className="container mt-4"><div className="alert alert-danger">{error}</div></div>;
  }

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Мои тренировки</h1>

      {workouts.length === 0 ? (
        <div className="card">
          <div className="card-body text-center">
            <p className="card-text">У вас пока нет добавленных тренировок.</p>
            <p className="card-text">Создайте план тренировки, выбрав упражнение из списка:</p>
            <button 
              className="btn btn-success mt-3"
              onClick={() => setShowPlanForm(true)}
            >
              Создать план тренировки
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="d-flex flex-column align-items-center gap-4">
            {workouts.map((workout) => (
              <div key={workout.id} className="card w-75">
                <div className="card-body">
                  <h2 className="card-title">
                    {workout.name || `Тренировка от ${workout.date}`}
                  </h2>
                  <p className="card-text">
                    <strong>Проработанные мышцы:</strong>{" "}
                    {workout.muscles_involved?.length
                      ? workout.muscles_involved.join(", ")
                      : "Не указано"}
                  </p>
                </div>
                <div className="card-footer text-center">
                  <Link to={`/workouts/${workout.id}`} className="btn btn-primary">
                    Подробнее...
                  </Link>
                </div>
              </div>
            ))}
          </div>
          <div className="text-center mt-4">
            <Link to="/workouts/new" className="btn btn-success">
              Добавить тренировку
            </Link>
          </div>
        </>
      )}

      {showPlanForm && (
        <div className="card mt-4">
          <div className="card-body">
            <h2 className="card-title text-center mb-4">Создать план тренировки</h2>
            <form onSubmit={handleCreatePlan}>
              <div className="form-group">
                <label htmlFor="exercise" className="form-label">
                  Выберите упражнение:
                </label>
                <select
                  id="exercise"
                  className="form-select"
                  value={selectedExercise}
                  onChange={(e) => setSelectedExercise(e.target.value)}
                  required
                >
                  <option value="">-- Выберите упражнение --</option>
                  {exercises.map((exercise) => (
                    <option key={exercise.id} value={exercise.id}>
                      {exercise.name} ({exercise.muscle_group})
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="startDate" className="form-label">
                  Дата первой тренировки:
                </label>
                <input
                  type="date"
                  id="startDate"
                  className="form-control"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  required
                  min={new Date().toISOString().split('T')[0]}
                />
              </div>

              <div className="form-group">
                <label htmlFor="frequency" className="form-label">
                  Периодичность (дней между тренировками):
                </label>
                <input
                  type="number"
                  id="frequency"
                  className="form-control"
                  value={frequencyDays}
                  onChange={(e) => setFrequencyDays(parseInt(e.target.value) || 1)}
                  min="1"
                  max="30"
                  required
                />
                <small className="text-secondary">
                  Тренировки будут автоматически добавляться в календарь каждые {frequencyDays} {frequencyDays === 1 ? 'день' : frequencyDays < 5 ? 'дня' : 'дней'}
                </small>
              </div>

              {planMessage && (
                <div className={`alert ${planMessage.includes('успешно') ? 'alert-success' : 'alert-danger'} mt-3`}>
                  {planMessage}
                </div>
              )}

              <div className="d-flex gap-2 mt-4">
                <button type="submit" className="btn btn-success w-100">
                  Создать план
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setShowPlanForm(false);
                    setPlanMessage("");
                    setSelectedExercise("");
                    setStartDate("");
                    setFrequencyDays(1);
                  }}
                >
                  Отмена
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {!showPlanForm && workouts.length > 0 && (
        <div className="text-center mt-4">
          <button 
            className="btn btn-outline-secondary"
            onClick={() => setShowPlanForm(true)}
          >
            Создать новый план тренировки
          </button>
        </div>
      )}
    </div>
  );
};

export default MyWorkouts;
